#!/bin/bash\n\n# Go依存関係の更新とビルド\n\necho \"🔄 Updating Go dependencies...\"\ncd data-sender\n\n# 依存関係をダウンロード\ngo mod download\n\n# 不要な依存関係を削除し、go.modを整理\ngo mod tidy\n\n# ビルド\necho \"🔨 Building data-sender...\"\ngo build -o data-sender main.go\n\nif [ $? -eq 0 ]; then\n    echo \"✅ Build successful!\"\n    echo \"\"\n    echo \"📋 Available commands:\"\n    echo \"  ./data-sender config         # Show configuration\"\n    echo \"  ./data-sender env-example    # Create .env.example\"\n    echo \"  ./data-sender enable <url> <key>  # Enable transmission\"\n    echo \"\"\n    echo \"🔧 Environment variable setup:\"\n    echo \"  1. ./data-sender env-example  # Create template\"\n    echo \"  2. cp .env.example .env       # Copy template\"\n    echo \"  3. edit .env with your values # Set actual values\"\nelse\n    echo \"❌ Build failed\"\n    exit 1\nfi\n